import org.json.JSONObject;
import java.util.List;
import java.util.ArrayList;
import java.util.Arrays;
import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;

class TestCases {
    @Test
    public void testConstructorInitializesFields() {
        JSONObject json = new JSONObject("{\"ownerId\": \"user1\"}");
        EnhancedLobbyManager lobby = new EnhancedLobbyManager(json, "lobby123");

        assertEquals("lobby123", lobby.getLobbyId());
        assertEquals("user1", lobby.getOwnerId());

        // Members list should be empty initially
        List<String> expectedMembers = new ArrayList<>();
        assertEquals(expectedMembers, lobby.getMembers());
    }

    @Test
    public void testAddMemberSuccessfully() {
        JSONObject lobbyJson = new JSONObject("{\"ownerId\": \"user1\"}");
        EnhancedLobbyManager lobby = new EnhancedLobbyManager(lobbyJson, "lobby123");

        JSONObject memberJson = new JSONObject("{\"id\": \"member1\"}");
        boolean result = lobby.addMember(memberJson);

        assertTrue(result);
        List<String> expectedMembers = Arrays.asList("member1");
        assertEquals(expectedMembers, lobby.getMembers());
    }

    @Test
    public void testAddDuplicateMember() {
        JSONObject lobbyJson = new JSONObject("{\"ownerId\": \"user1\"}");
        EnhancedLobbyManager lobby = new EnhancedLobbyManager(lobbyJson, "lobby123");

        JSONObject memberJson = new JSONObject("{\"id\": \"member1\"}");
        lobby.addMember(memberJson);

        // Try to add the same member again
        boolean result = lobby.addMember(memberJson);

        assertFalse(result);
        List<String> expectedMembers = Arrays.asList("member1");
        assertEquals(expectedMembers, lobby.getMembers());
    }
}

