        JSONObject memberJson1 = new JSONObject();
        memberJson1.put("id", "member1");
        memberJson1.put("name", "Bob");
        memberJson1.put("colorIndex", 2);
        memberJson1.put("isReady", false);

        JSONObject memberJson2 = new JSONObject();
        memberJson2.put("id", "member1");
        memberJson2.put("name", "Bobby");
