import static org.junit.jupiter.api.Assertions.*;
import org.json.JSONObject;
import org.junit.jupiter.api.Test;
import java.util.Map;

class TestCases extends junit.framework.TestCase {
    @Test
    public void testSettingsInitializationFromConstructor() {
        // Create a lobby JSON with settings
        JSONObject settingsJson = new JSONObject();
        settingsJson.put("maxPlayers", "4");
        settingsJson.put("gameMode", "deathmatch");

        JSONObject lobbyJson = new JSONObject();
        lobbyJson.put("ownerId", "owner123");
        lobbyJson.put("settings", settingsJson);

        EnhancedLobbyManager manager = new EnhancedLobbyManager(lobbyJson, "lobby1");

        Map<String, String> settings = manager.getSettings();
        assertEquals("4", settings.get("maxPlayers"));
        assertEquals("deathmatch", settings.get("gameMode"));
        assertEquals(2, settings.size());
    }

    @Test
    public void testUpdateSettingsMethod() {
        JSONObject lobbyJson = new JSONObject();
        lobbyJson.put("ownerId", "owner123");

        EnhancedLobbyManager manager = new EnhancedLobbyManager(lobbyJson, "lobby1");

        // Initially empty settings
        assertTrue(manager.getSettings().isEmpty());

        // Update settings
        JSONObject newSettings = new JSONObject();
        newSettings.put("map", "forest");
        newSettings.put("timeLimit", "30");

        manager.updateSettings(newSettings);

        Map<String, String> settings = manager.getSettings();
        assertEquals("forest", settings.get("map"));
        assertEquals("30", settings.get("timeLimit"));
        assertEquals(2, settings.size());
    }

    @Test
    public void testSettingsUpdateOverwritesExisting() {
        // Create a lobby JSON with initial settings
        JSONObject settingsJson = new JSONObject();
        settingsJson.put("maxPlayers", "4");
        settingsJson.put("gameMode", "deathmatch");

        JSONObject lobbyJson = new JSONObject();
        lobbyJson.put("ownerId", "owner123");
        lobbyJson.put("settings", settingsJson);

        EnhancedLobbyManager manager = new EnhancedLobbyManager(lobbyJson, "lobby1");

        // Verify initial settings
        Map<String, String> settings = manager.getSettings();
        assertEquals("4", settings.get("maxPlayers"));
        assertEquals("deathmatch", settings.get("gameMode"));

        // Update settings, overwriting one and adding a new one
        JSONObject updatedSettings = new JSONObject();
        updatedSettings.put("maxPlayers", "8"); // Overwrite
        updatedSettings.put("difficulty", "hard"); // New setting

        manager.updateSettings(updatedSettings);

        settings = manager.getSettings();
        assertEquals("8", settings.get("maxPlayers")); // Overwritten
        assertEquals("deathmatch", settings.get("gameMode")); // Unchanged
        assertEquals("hard", settings.get("difficulty")); // New
        assertEquals(3, settings.size());
    }
}

