import org.json.JSONObject;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.Iterator;
import java.util.List;
import java.util.Map;

enum LobbyStatus {
    ACTIVE, IN_GAME, CLOSED, MAINTENANCE
}

class LobbyMember {
    private String id;
    private String name;
    private int colorIndex;
    private boolean isReady;

    public LobbyMember(String id, String name, int colorIndex, boolean isReady) {
        this.id = id;
        this.name = name;
        this.colorIndex = colorIndex;
        this.isReady = isReady;
    }

    public String getId() { return id; }
    public String getName() { return name; }
    public int getColorIndex() { return colorIndex; }
    public boolean isReady() { return isReady; }
}

class EnhancedLobbyManager {
    private String lobbyId;
    private String ownerId;
    private List<LobbyMember> members;
    private Map<String, String> settings;
    private LobbyStatus status;

    public EnhancedLobbyManager(JSONObject lobbyJson, String lobbyId) {
        this.lobbyId = lobbyId;
        this.ownerId = lobbyJson.getString("ownerId");
        this.members = new ArrayList<>();
        this.settings = new HashMap<>();
        this.status = LobbyStatus.ACTIVE;

        // Parse initial settings from lobbyJson
        if (lobbyJson.has("settings")) {
            JSONObject settingsJson = lobbyJson.getJSONObject("settings");
            updateSettings(settingsJson);
        }
    }

    public boolean addMember(JSONObject memberJson) {
        String memberId = memberJson.getString("id");
        // Check if member already exists
        for (LobbyMember member : members) {
            if (member.getId().equals(memberId)) {
                return false;
            }
        }

        String name = memberJson.getString("name");
        int colorIndex = memberJson.getInt("colorIndex");
        boolean isReady = memberJson.getBoolean("isReady");

        members.add(new LobbyMember(memberId, name, colorIndex, isReady));
        return true;
    }

    public boolean removeMember(String memberId) {
        for (int i = 0; i < members.size(); i++) {
            if (members.get(i).getId().equals(memberId)) {
                members.remove(i);
                return true;
            }
        }
        return false;
    }

    public void updateSettings(JSONObject settingsJson) {
        Iterator<String> keys = settingsJson.keys();
        while (keys.hasNext()) {
            String key = keys.next();
            String value = settingsJson.getString(key);
            settings.put(key, value);
        }
    }

    public void setStatus(LobbyStatus status) {
        this.status = status;
    }

    public String getLobbyId() { return lobbyId; }
    public String getOwnerId() { return ownerId; }
    public List<LobbyMember> getMembers() { return new ArrayList<>(members); }
    public Map<String, String> getSettings() { return new HashMap<>(settings); }
    public LobbyStatus getStatus() { return status; }
}

