import static org.junit.jupiter.api.Assertions.*;
import org.json.JSONObject;
import org.junit.jupiter.api.Test;

class TestCases extends junit.framework.TestCase {

    @Test
    public void testLobbyStatusEnumExists() {
        // This test ensures the enum exists and has the correct values
        assertNotNull(LobbyStatus.ACTIVE);
        assertNotNull(LobbyStatus.IN_GAME);
        assertNotNull(LobbyStatus.CLOSED);
        assertNotNull(LobbyStatus.MAINTENANCE);
    }

    @Test
    public void testStatusFieldAndSetStatus() {
        JSONObject json = new JSONObject();
        json.put("ownerId", "owner1");

        EnhancedLobbyManager manager = new EnhancedLobbyManager(json, "lobby1");

        // Check initial status is ACTIVE
        assertEquals(LobbyStatus.ACTIVE, manager.getStatus());

        // Change status and verify
        manager.setStatus(LobbyStatus.IN_GAME);
        assertEquals(LobbyStatus.IN_GAME, manager.getStatus());

        manager.setStatus(LobbyStatus.MAINTENANCE);
        assertEquals(LobbyStatus.MAINTENANCE, manager.getStatus());
    }

    @Test
    public void testRemoveMemberSuccess() {
        JSONObject json = new JSONObject();
        json.put("ownerId", "owner1");

        EnhancedLobbyManager manager = new EnhancedLobbyManager(json, "lobby1");

        JSONObject memberJson = new JSONObject();
        memberJson.put("id", "member1");
        memberJson.put("name", "Alice");
        memberJson.put("colorIndex", 1);
        memberJson.put("isReady", true);

        manager.addMember(memberJson);

        // Verify member was added
        assertEquals(1, manager.getMembers().size());

        // Remove member and verify success
        boolean result = manager.removeMember("member1");
        assertTrue(result);
        assertEquals(0, manager.getMembers().size());
    }

    @Test
    public void testRemoveMemberNotFound() {
        JSONObject json = new JSONObject();
        json.put("ownerId", "owner1");

        EnhancedLobbyManager manager = new EnhancedLobbyManager(json, "lobby1");

        // Try to remove a member that doesn't exist
        boolean result = manager.removeMember("nonexistent");
        assertFalse(result);
        assertEquals(0, manager.getMembers().size());
    }

    @Test
    public void testRemoveMemberFromMultiple() {
        JSONObject json = new JSONObject();
        json.put("ownerId", "owner1");

        EnhancedLobbyManager manager = new EnhancedLobbyManager(json, "lobby1");

        // Add multiple members
        JSONObject member1 = new JSONObject();
        member1.put("id", "member1");
        member1.put("name", "Alice");
        member1.put("colorIndex", 1);
        member1.put("isReady", true);
        manager.addMember(member1);

        JSONObject member2 = new JSONObject();
        member2.put("id", "member2");
        member2.put("name", "Bob");
        member2.put("colorIndex", 2);
        member2.put("isReady", false);
        manager.addMember(member2);

        // Verify both members were added
        assertEquals(2, manager.getMembers().size());

        // Remove first member
        boolean result = manager.removeMember("member1");
        assertTrue(result);
        assertEquals(1, manager.getMembers().size());
        assertEquals("member2", manager.getMembers().get(0).getId());
    }
}

