import org.json.JSONObject;
import java.util.List;
import java.util.Map;
import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;

class TestCases {
    @Test
    public void testChangeOwnerWithValidMember() {
        JSONObject lobbyJson = new JSONObject();
        lobbyJson.put("ownerId", "owner1");

        EnhancedLobbyManager lobby = new EnhancedLobbyManager(lobbyJson, "lobby1");

        JSONObject memberJson = new JSONObject();
        memberJson.put("id", "member1");
        memberJson.put("name", "Member One");
        memberJson.put("colorIndex", 1);
        memberJson.put("isReady", true);

        lobby.addMember(memberJson);

        lobby.changeOwner("member1");

        assertEquals("member1", lobby.getOwnerId());
    }

    @Test
    public void testChangeOwnerWithInvalidMember() {
        JSONObject lobbyJson = new JSONObject();
        lobbyJson.put("ownerId", "owner1");

        EnhancedLobbyManager lobby = new EnhancedLobbyManager(lobbyJson, "lobby1");

        assertThrows(IllegalArgumentException.class, () -> {
            lobby.changeOwner("nonexistent");
        });
    }

    @Test
    public void testDefensiveCopyOfMembers() {
        JSONObject lobbyJson = new JSONObject();
        lobbyJson.put("ownerId", "owner1");

        EnhancedLobbyManager lobby = new EnhancedLobbyManager(lobbyJson, "lobby1");

        JSONObject memberJson = new JSONObject();
        memberJson.put("id", "member1");
        memberJson.put("name", "Member One");
        memberJson.put("colorIndex", 1);
        memberJson.put("isReady", true);

        lobby.addMember(memberJson);

        List<LobbyMember> members = lobby.getMembers();
        members.clear(); // This should not affect the lobby's internal members list

        assertEquals(1, lobby.getMembers().size());
    }

    @Test
    public void testDefensiveCopyOfSettings() {
        JSONObject lobbyJson = new JSONObject();
        lobbyJson.put("ownerId", "owner1");
        JSONObject settingsJson = new JSONObject();
        settingsJson.put("maxPlayers", "4");
        lobbyJson.put("settings", settingsJson);

        EnhancedLobbyManager lobby = new EnhancedLobbyManager(lobbyJson, "lobby1");

        Map<String, String> settings = lobby.getSettings();
        settings.put("newSetting", "value"); // This should not affect the lobby's internal settings

        assertFalse(lobby.getSettings().containsKey("newSetting"));
        assertEquals("4", lobby.getSettings().get("maxPlayers"));
    }
}

