import java.util.HashMap;
import java.util.Map;

class UrlRouter {
    private Map<String, String> routeMap;
    private String defaultRoute;

    public UrlRouter(Map<String, String> routes, String defaultRoute) {
        this.routeMap = new HashMap<>(routes);
        this.defaultRoute = defaultRoute;
    }

    public String resolveRoute(String path) {
        if (routeMap.containsKey(path)) {
            return routeMap.get(path);
        }

        // Check for parameterized routes
        for (Map.Entry<String, String> entry : routeMap.entrySet()) {
            String routePattern = entry.getKey();
            if (isParameterizedRoute(routePattern) && matchesParameterizedRoute(routePattern, path)) {
                String parameter = extractParameter(routePattern, path);
                return constructParameterizedTemplate(entry.getValue(), parameter);
            }
        }

        return defaultRoute;
    }

    private boolean isParameterizedRoute(String route) {
        if (route == null) {
            return false;
        }
        int openBraceIndex = route.indexOf("{");
        int closeBraceIndex = route.indexOf("}");
        return openBraceIndex != -1 && closeBraceIndex != -1 && openBraceIndex < closeBraceIndex;
    }

    public boolean matchesParameterizedRoute(String routePattern, String path) {
        if (routePattern == null || path == null) {
            return false;
        }

        String[] patternSegments = routePattern.split("/");
        String[] pathSegments = path.split("/");

        if (patternSegments.length != pathSegments.length) {
            return false;
        }

        for (int i = 0; i < patternSegments.length; i++) {
            String patternSegment = patternSegments[i];
            String pathSegment = pathSegments[i];

            if (patternSegment.startsWith("{") && patternSegment.endsWith("}")) {
                continue;
            }

            if (!patternSegment.equals(pathSegment)) {
                return false;
            }
        }

        return true;
    }

    public String extractParameter(String routePattern, String path) {
        if (routePattern == null || path == null) {
            return null;
        }

        String[] patternSegments = routePattern.split("/");
        String[] pathSegments = path.split("/");

        if (patternSegments.length != pathSegments.length) {
            return null;
        }

        for (int i = 0; i < patternSegments.length; i++) {
            String patternSegment = patternSegments[i];
            if (patternSegment.startsWith("{") && patternSegment.endsWith("}")) {
                return pathSegments[i];
            }
        }

        return null;
    }

    public String constructParameterizedTemplate(String templatePattern, String parameter) {
        if (templatePattern == null || parameter == null) {
            return templatePattern;
        }
        return templatePattern.replace("{id}", parameter);
    }
}

