import java.util.*;
import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;

class TestCases {

    @Test
    public void testConstructParameterizedTemplate() {
        UrlRouter router = new UrlRouter(new HashMap<>(), "default");

        // Test basic replacement
        assertEquals("user_123_profile", router.constructParameterizedTemplate("user_{id}_profile", "123"));

        // Test multiple replacements
        assertEquals("test_test", router.constructParameterizedTemplate("{id}_{id}", "test"));

        // Test no replacement needed
        assertEquals("user_profile", router.constructParameterizedTemplate("user_profile", "123"));

        // Test null parameter
        assertEquals("user_{id}_profile", router.constructParameterizedTemplate("user_{id}_profile", null));

        // Test null template
        assertNull(router.constructParameterizedTemplate(null, "123"));
    }

    @Test
    public void testResolveRouteWithParameterizedRoutes() {
        Map<String, String> routes = new HashMap<>();
        routes.put("/users/{id}", "user_{id}_profile");
        routes.put("/posts/{id}/comments", "post_{id}_comments");
        routes.put("/static", "static_page");

        UrlRouter router = new UrlRouter(routes, "default");

        // Test exact match
        assertEquals("static_page", router.resolveRoute("/static"));

        // Test parameterized route match
        assertEquals("user_123_profile", router.resolveRoute("/users/123"));

        // Test another parameterized route match
        assertEquals("post_456_comments", router.resolveRoute("/posts/456/comments"));

        // Test parameterized route with different ID
        assertEquals("user_abc_profile", router.resolveRoute("/users/abc"));

        // Test non-matching route
        assertEquals("default", router.resolveRoute("/unknown"));

        // Test non-matching parameterized route
        assertEquals("default", router.resolveRoute("/users/123/extra"));
    }
}

