import java.util.*;

public class AdvancedTileCalculator {
    public static class TileConfiguration {
        private int x;
        private int y;

        public TileConfiguration(int x, int y) {
            this.x = x;
            this.y = y;
        }

        public int getX() {
            return x;
        }

        public int getY() {
            return y;
        }

        @Override
        public String toString() {
            return "TileConfiguration(x=" + x + ", y=" + y + ")";
        }

        @Override
        public boolean equals(Object obj) {
            if (this == obj) return true;
            if (obj == null || getClass() != obj.getClass()) return false;
            TileConfiguration that = (TileConfiguration) obj;
            return x == that.x && y == that.y;
        }

        @Override
        public int hashCode() {
            return Objects.hash(x, y);
        }
    }

    public TileConfiguration calculateAdvancedTile(List<TileConfiguration> neighbors) {
        if (neighbors == null || neighbors.isEmpty()) {
            return new TileConfiguration(0, 0);
        }

        boolean hasUp = false;
        boolean hasDown = false;
        boolean hasLeft = false;
        boolean hasRight = false;
        boolean hasUpLeft = false;
        boolean hasUpRight = false;
        boolean hasDownLeft = false;
        boolean hasDownRight = false;

        for (TileConfiguration neighbor : neighbors) {
            if (neighbor.getX() == 0 && neighbor.getY() == -1) {
                hasUp = true;
            } else if (neighbor.getX() == 0 && neighbor.getY() == 1) {
                hasDown = true;
            } else if (neighbor.getX() == -1 && neighbor.getY() == 0) {
                hasLeft = true;
            } else if (neighbor.getX() == 1 && neighbor.getY() == 0) {
                hasRight = true;
            } else if (neighbor.getX() == -1 && neighbor.getY() == -1) {
                hasUpLeft = true;
            } else if (neighbor.getX() == 1 && neighbor.getY() == -1) {
                hasUpRight = true;
            } else if (neighbor.getX() == -1 && neighbor.getY() == 1) {
                hasDownLeft = true;
            } else if (neighbor.getX() == 1 && neighbor.getY() == 1) {
                hasDownRight = true;
            }
        }

        // Check for T-junctions (specific cases first)
        if (hasUp && hasDown && hasRight && !hasLeft) {
            return new TileConfiguration(2, 2);
        }
        if (hasUp && hasDown && hasLeft && !hasRight) {
            return new TileConfiguration(3, 2);
        }
        if (hasUp && hasLeft && hasRight && !hasDown) {
            return new TileConfiguration(4, 2);
        }
        if (hasDown && hasLeft && hasRight && !hasUp) {
            return new TileConfiguration(4, 0);
        }

        // Check for full cross (all four directions)
        if (hasUp && hasDown && hasLeft && hasRight) {
            // Check if all diagonals are present
            if (hasUpLeft && hasUpRight && hasDownLeft && hasDownRight) {
                return new TileConfiguration(4, 2);
            } else {
                return new TileConfiguration(6, 0);
            }
        }

        // Check for fully surrounded tile (all 8 neighbors)
        if (hasUp && hasDown && hasLeft && hasRight &&
            hasUpLeft && hasUpRight && hasDownLeft && hasDownRight) {
            return new TileConfiguration(4, 2);
        }

        // Check for diagonal dependencies
        if (hasUp && hasLeft && hasUpLeft) {
            return new TileConfiguration(3, 0);
        } else if (hasUp && hasRight && hasUpRight) {
            return new TileConfiguration(2, 0);
        } else if (hasDown && hasLeft && hasDownLeft) {
            return new TileConfiguration(3, 1);
        } else if (hasDown && hasRight && hasDownRight) {
            return new TileConfiguration(2, 1);
        }
        // Check for vertical passage: only UP and DOWN neighbors
        else if (hasUp && hasDown && !hasLeft && !hasRight) {
            return new TileConfiguration(5, 2);
        }
        // Check for horizontal passage: only LEFT and RIGHT neighbors
        else if (hasLeft && hasRight && !hasUp && !hasDown) {
            return new TileConfiguration(1, 2);
        }
        // Check for dead ends - only that direction and no others
        else if (hasUp && !hasDown && !hasLeft && !hasRight && !hasUpLeft && !hasUpRight && !hasDownLeft && !hasDownRight) {
            return new TileConfiguration(1, 0);
        }
        else if (!hasUp && hasDown && !hasLeft && !hasRight && !hasUpLeft && !hasUpRight && !hasDownLeft && !hasDownRight) {
            return new TileConfiguration(0, 2);
        }
        else if (!hasUp && !hasDown && hasLeft && !hasRight && !hasUpLeft && !hasUpRight && !hasDownLeft && !hasDownRight) {
            return new TileConfiguration(5, 1);
        }
        else if (!hasUp && !hasDown && !hasLeft && hasRight && !hasUpLeft && !hasUpRight && !hasDownLeft && !hasDownRight) {
            return new TileConfiguration(5, 0);
        }
        else {
            return new TileConfiguration(1, 1);
        }
    }
}

