import java.util.HashMap;
import java.util.Map;
import java.util.Set;
import java.util.HashSet;
import java.util.Collections;
import java.util.List;
import java.util.ArrayList;

class ConfigurationManager {
    private Map<String, Object> configMap;
    private Set<String> requiredKeys;
    private Map<String, List<Validator>> validators;

    public ConfigurationManager(Set<String> requiredKeys) {
        this(requiredKeys, null);
    }

    public ConfigurationManager(Set<String> requiredKeys, Map<String, List<Validator>> validators) {
        this.configMap = new HashMap<>();
        this.requiredKeys = requiredKeys != null ? new HashSet<>(requiredKeys) : Collections.emptySet();
        this.validators = validators != null ? new HashMap<>(validators) : new HashMap<>();
    }

    public <T> T getConfigValue(String key, Class<T> type) {
        return type.cast(configMap.get(key));
    }

    public void loadConfiguration(Map<String, Object> properties) throws ConfigurationException {
        if (properties == null) {
            properties = Collections.emptyMap();
        }

        Set<String> missingKeys = new HashSet<>();
        for (String key : requiredKeys) {
            if (!properties.containsKey(key)) {
                missingKeys.add(key);
            }
        }

        if (!missingKeys.isEmpty()) {
            throw new ConfigurationException("Missing required keys: " + missingKeys);
        }

        configMap.clear();
        configMap.putAll(properties);

        validateConfiguration();
    }

    private void validateConfiguration() throws ConfigurationException {
        for (Map.Entry<String, List<Validator>> entry : validators.entrySet()) {
            String key = entry.getKey();
            List<Validator> keyValidators = entry.getValue();

            // Only validate if the key exists in the configuration
            if (configMap.containsKey(key)) {
                Object value = configMap.get(key);

                for (Validator validator : keyValidators) {
                    if (!validator.isValid(value)) {
                        throw new ConfigurationException(validator.getErrorMessage());
                    }
                }
            }
        }
    }
}

interface Validator {
    boolean isValid(Object value);
    String getErrorMessage();
}

class ConfigurationException extends Exception {
    public ConfigurationException(String message) {
        super(message);
    }
}

