import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.TimeUnit;

class TestCases {

    @Test
    public void testGetActiveThreadCount() throws InterruptedException {
        ThreadResourceManager manager = new ThreadResourceManager();

        // Initially should be 0
        assertEquals(0, manager.getActiveThreadCount());

        // Create one thread
        CountDownLatch latch = new CountDownLatch(1);
        manager.createDaemonThread("test-thread-1", () -> {
            try {
                latch.await(5, TimeUnit.SECONDS);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        });

        assertEquals(1, manager.getActiveThreadCount());

        // Create another thread
        manager.createDaemonThread("test-thread-2", () -> {
            try {
                Thread.sleep(1000);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        });

        assertEquals(2, manager.getActiveThreadCount());

        // Stop one thread
        manager.stopDaemonThread("test-thread-1");
        latch.countDown(); // Allow the thread to finish

        // Give it a moment to be removed
        Thread.sleep(100);

        assertEquals(1, manager.getActiveThreadCount());

        // Stop the other thread
        manager.stopDaemonThread("test-thread-2");

        // Give it a moment to be removed
        Thread.sleep(100);

        assertEquals(0, manager.getActiveThreadCount());
    }
}

