import java.util.concurrent.ConcurrentHashMap;
import java.util.Map;
import java.util.concurrent.Executors;
import java.util.concurrent.ScheduledExecutorService;
import java.util.concurrent.TimeUnit;

class ThreadResourceManager {
    private final Map<String, Thread> threads = new ConcurrentHashMap<>();
    private final ScheduledExecutorService scheduler = Executors.newSingleThreadScheduledExecutor();

    public ThreadResourceManager() {
        scheduler.scheduleAtFixedRate(this::cleanupDeadThreads, 1, 1, TimeUnit.SECONDS);
    }

    public boolean createDaemonThread(String threadName, Runnable task) {
        if (threadName == null || task == null) {
            return false;
        }

        Thread thread = new Thread(task, threadName);
        thread.setDaemon(true);
        thread.start();
        threads.put(threadName, thread);

        return true;
    }

    public boolean stopDaemonThread(String threadName) {
        if (threadName == null) {
            return false;
        }

        Thread thread = threads.remove(threadName);
        if (thread != null) {
            thread.interrupt();
            return true;
        }

        return false;
    }

    public int getActiveThreadCount() {
        return threads.size();
    }

    private void cleanupDeadThreads() {
        threads.entrySet().removeIf(entry -> !entry.getValue().isAlive());
    }
}

