import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.BeforeEach;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.TimeUnit;

class TestCases {
    private ThreadResourceManager manager;

    @BeforeEach
    void setUp() {
        manager = new ThreadResourceManager();
    }

    @Test
    void testShutdownInterruptsAllThreads() throws InterruptedException {
        CountDownLatch latch = new CountDownLatch(2);

        // Create two daemon threads that will wait
        manager.createDaemonThread("thread1", () -> {
            try {
                Thread.sleep(10000); // Long sleep
            } catch (InterruptedException e) {
                // Expected when interrupted
                latch.countDown();
            }
        });

        manager.createDaemonThread("thread2", () -> {
            try {
                Thread.sleep(10000); // Long sleep
            } catch (InterruptedException e) {
                // Expected when interrupted
                latch.countDown();
            }
        });

        // Give threads time to start
        Thread.sleep(100);

        // Verify threads are active
        assertEquals(2, manager.getActiveThreadCount());

        // Shutdown the manager
        manager.shutdown();

        // Wait for threads to be interrupted
        boolean completed = latch.await(1, TimeUnit.SECONDS);
        assertTrue(completed, "All threads should be interrupted");
    }

    @Test
    void testShutdownClearsThreadMap() {
        // Create a thread
        manager.createDaemonThread("testThread", () -> {
            try {
                Thread.sleep(1000);
            } catch (InterruptedException e) {
                // Expected
            }
        });

        // Verify thread was created
        assertEquals(1, manager.getActiveThreadCount());

        // Shutdown
        manager.shutdown();

        // Verify map is cleared
        assertEquals(0, manager.getActiveThreadCount());
    }

    @Test
    void testThreadNameUniqueness() {
        // Create first thread
        boolean firstCreated = manager.createDaemonThread("uniqueThread", () -> {
            try {
                Thread.sleep(1000);
            } catch (InterruptedException e) {
                // Expected
            }
        });

        // Try to create second thread with same name
        boolean secondCreated = manager.createDaemonThread("uniqueThread", () -> {
            // Different task
        });

        // First should succeed, second should fail
        assertTrue(firstCreated);
        assertFalse(secondCreated);

        // Only one thread should exist
        assertEquals(1, manager.getActiveThreadCount());
    }
}

