import java.util.*;

public class EnhancedUssmsCardErrorHandler {
    public enum UssmsCardErrType {
        SUCCESS(0, "Operation successful"),
        FORMAT_ERR(1, "Invalid format"),
        CARDINFO_ERR(2, "Invalid card information"),
        CHARGE_ERR(3, "Charge error"),
        EXPIRED_CARD(4, "Card has expired"),
        ALREADY_USED(5, "Card already used"),
        SYSTEM_ERROR(6, "System error");

        private final int code;
        private final String message;
        private static final Map<Integer, UssmsCardErrType> CODE_MAP = new HashMap<>();

        static {
            for (UssmsCardErrType type : values()) {
                CODE_MAP.put(type.getCode(), type);
            }
        }

        UssmsCardErrType(int code, String message) {
            this.code = code;
            this.message = message;
        }

        public int getCode() {
            return code;
        }

        public String getMessage() {
            return message;
        }

        public boolean isSuccess() {
            return this == SUCCESS;
        }

        public boolean isTemporaryError() {
            return this == CHARGE_ERR || this == SYSTEM_ERROR;
        }

        public static UssmsCardErrType fromCode(int code) {
            UssmsCardErrType result = CODE_MAP.get(code);
            if (result == null) {
                throw new IllegalArgumentException("No UssmsCardErrType with code: " + code);
            }
            return result;
        }
    }

    public static String processError(int errorCode) {
        for (UssmsCardErrType errType : UssmsCardErrType.values()) {
            if (errType.getCode() == errorCode) {
                return "Error " + errorCode + ": " + errType.getMessage();
            }
        }
        return "Error " + errorCode + ": Unknown error";
    }
}

