    @Test
    public void testPublishMessageWhenReady() {
        Map<String, Object> config = new HashMap<>();
        config.put("status", "READY");
        config.put("messageCount", 5);

        long beforePublish = System.currentTimeMillis();
        Map<String, Object> result = mqConfigurator.publishMessage(config, "Test message");
        long afterPublish = System.currentTimeMillis();

        assertEquals("Test message", result.get("lastMessage"));
        assertEquals(6, result.get("messageCount"));
        assertTrue((Long) result.get("lastPublished") >= beforePublish);
        assertTrue((Long) result.get("lastPublished") <= afterPublish);
    }

    @Test
    public void testPublishMessageWhenNotReady() {
        Map<String, Object> config = new HashMap<>();
        config.put("status", "NOT_READY");
        config.put("messageCount", 3);

        Map<String, Object> result = mqConfigurator.publishMessage(config, "Test message");

        assertFalse(result.containsKey("lastMessage"));
        assertEquals(3, result.get("messageCount"));
    }

    @Test
    public void testPublishMessageWithNoMessageCount() {
        Map<String, Object> config = new HashMap<>();
        config.put("status", "READY");

        Map<String, Object> result = mqConfigurator.publishMessage(config, "First message");

        assertEquals("First message", result.get("lastMessage"));
        assertEquals(1, result.get("messageCount"));
    }
