class GameEngine {
    private Player player;

    public GameEngine() {
        this.player = new Player();
    }

    public void startNewGame(int startX, int startY) {
        player.setPosition(startX, startY);
    }

    public int[] handlePlayerMovement(String direction) {
        switch(direction) {
            case "UP":
                player.move(0, -1);
                break;
            case "DOWN":
                player.move(0, 1);
                break;
            case "LEFT":
                player.move(-1, 0);
                break;
            case "RIGHT":
                player.move(1, 0);
                break;
            default:
                // No movement for invalid directions
                break;
        }
        return player.getPosition();
    }
}

class Player {
    private int x;
    private int y;

    public Player() {
        this.x = 0;
        this.y = 0;
    }

    public void setPosition(int x, int y) {
        this.x = x;
        this.y = y;
    }

    public int[] getPosition() {
        return new int[]{x, y};
    }

    public void move(int deltaX, int deltaY) {
        this.x += deltaX;
        this.y += deltaY;
    }
}

