        GameEngine game = new GameEngine();
        game.startNewGame(100, 100);

        // Click outside 50 units
        boolean result = game.handleMouseClick(200, 200); // Distance is sqrt(10000+10000) = sqrt(20000)  141.42
        assertFalse(result);
        assertEquals(0, game.getScore());
    }

    @Test
    void testMouseClickBeforeGameStart() {
        GameEngine game = new GameEngine();
        // Game not started
        boolean result = game.handleMouseClick(0, 0);
        assertFalse(result);
        assertEquals(0, game.getScore());
    }

    @Test
    void testMouseClickExactlyAtBoundary() {
        GameEngine game = new GameEngine();
        game.startNewGame(0, 0);

        // Click exactly 50 units away
        boolean result = game.handleMouseClick(30, 40); // Distance is sqrt(900+1600) = sqrt(2500) = 50
        assertTrue(result);
        assertEquals(10, game.getScore());
    }

    @Test
    void testMultipleClicks() {
        GameEngine game = new GameEngine();
        game.startNewGame(50, 50);

        // First click within range
        game.handleMouseClick(60, 60); // Distance = sqrt(100+100) = sqrt(200)  14.14
        assertEquals(10, game.getScore());

        // Second click within range
        game.handleMouseClick(70, 70); // Distance = sqrt(400+400) = sqrt(800)  28.28
        assertEquals(20, game.getScore());

        // Third click outside range
        game.handleMouseClick(200, 200); // Distance = sqrt(150^2 + 150^2) = sqrt(45000)  212.13
        assertEquals(20, game.getScore());
    }
}

