import java.util.*;

class GameEngine {
    private Player player;
    private Map<String, Boolean> gameState;
    private int score;

    public GameEngine() {
        this.player = new Player();
        this.gameState = new HashMap<>();
        this.score = 0;
        initializeGameState();
    }

    private void initializeGameState() {
        gameState.put("gameStarted", false);
        gameState.put("gamePaused", false);
        gameState.put("gameOver", false);
    }

    public void startNewGame(int startX, int startY) {
        player.setPosition(startX, startY);
        gameState.put("gameStarted", true);
        gameState.put("gamePaused", false);
        gameState.put("gameOver", false);
        this.score = 0; // Reset score when starting new game
    }

    public int[] handlePlayerMovement(String direction) {
        // Only allow movement when game is started and not paused
        if (!gameState.get("gameStarted") || gameState.get("gamePaused")) {
            return player.getPosition();
        }

        switch(direction) {
            case "UP":
                player.move(0, -1);
                break;
            case "DOWN":
                player.move(0, 1);
                break;
            case "LEFT":
                player.move(-1, 0);
                break;
            case "RIGHT":
                player.move(1, 0);
                break;
            default:
                // No movement for invalid directions
                break;
        }
        return player.getPosition();
    }

    public boolean handleMouseClick(int clickX, int clickY) {
        // Only process clicks when game is started and not paused
        if (!gameState.get("gameStarted") || gameState.get("gamePaused")) {
            return false;
        }

        int[] playerPos = player.getPosition();
        int playerX = playerPos[0];
        int playerY = playerPos[1];

        // Calculate distance between click and player
        double distance = Math.sqrt(Math.pow(clickX - playerX, 2) + Math.pow(clickY - playerY, 2));

        if (distance <= 50) {
            this.score += 10;
            return true;
        }

        return false;
    }

    public int getScore() {
        return this.score;
    }

    // Return a copy of the gameState map to prevent external modification
    public Map<String, Boolean> getGameState() {
        return new HashMap<>(gameState);
    }

    // Getter methods for testing
    public boolean isGameStarted() {
        return gameState.get("gameStarted");
    }

    public boolean isGamePaused() {
        return gameState.get("gamePaused");
    }

    public boolean isGameOver() {
        return gameState.get("gameOver");
    }

    // Helper method for testing to set game state directly
    public void setGameState(String key, boolean value) {
        gameState.put(key, value);
    }
}

