import java.util.Map;

class EnhancedActivityManager {
    private Map<String, String> activityParams;
    private Locale currentLocale;
    private String lastErrorMessage;

    public EnhancedActivityManager() {
        // Initialize fields
        this.activityParams = new HashMap<>();
        this.currentLocale = Locale.getDefault();
        this.lastErrorMessage = null;
    }

    public boolean navigateTo(String targetActivity, Map<String, String> params) {
        if (targetActivity == null || targetActivity.isEmpty()) {
            this.lastErrorMessage = "Target activity cannot be null or empty";
            return false;
        }

        this.activityParams = params != null ? params : new HashMap<>();
        this.lastErrorMessage = null;  // Clear error on success
        return true;
    }

    public boolean changeLocale(String language, String country) {
        if (language == null || country == null) {
            this.lastErrorMessage = "Language and country cannot be null";
            return false;
        }

        this.currentLocale = new Locale(language, country);
        Locale.setDefault(this.currentLocale);
        this.lastErrorMessage = null;  // Clear error on success
        return true;
    }

    public String getLastErrorMessage() {
        return this.lastErrorMessage;
    }

    public Map<String, Object> handleError(String errorMessage, boolean isBlocking) {
        this.lastErrorMessage = errorMessage;

        Map<String, Object> errorMap = new HashMap<>();
        errorMap.put("message", errorMessage);
        errorMap.put("isBlocking", isBlocking);
        errorMap.put("timestamp", System.currentTimeMillis());
        errorMap.put("action", isBlocking ? "terminate" : "continue");

        return errorMap;
    }

    public Locale getCurrentLocale() {
        return this.currentLocale;
    }

    public void clearParameters() {
        this.activityParams.clear();
    }
}

