import java.util.List;
import java.util.Map;
import java.util.HashMap;
import java.util.Collections;
import java.util.stream.Collectors;

class LogAnalyzer {

    public Map<String, Object> analyzeLogs(List<SysLog> logs) {
        Map<String, Object> result = new HashMap<>();

        if (logs == null || logs.isEmpty()) {
            result.put("error", "No logs provided for analysis");
            return result;
        }

        result.put("totalLogs", logs.size());

        // Compute user frequency using streams
        Map<String, Long> userFrequency = logs.stream()
            .collect(Collectors.groupingBy(SysLog::getUsername, Collectors.counting()));
        result.put("userFrequency", userFrequency);

        // Find the most active user
        String mostActiveUser = Collections.max(userFrequency.entrySet(), Map.Entry.comparingByValue()).getKey();
        result.put("mostActiveUser", mostActiveUser);

        return result;
    }

    public static class SysLog {
        private String username;
        private String message;
        private long time;
        private String severity;

        public SysLog(String username, String message, long time, String severity) {
            this.username = username;
            this.message = message;
            this.time = time;
            this.severity = severity;
        }

        public String getUsername() { return username; }
        public String getMessage() { return message; }
        public long getTime() { return time; }
        public String getSeverity() { return severity; }
    }
}

