import java.util.List;
import java.util.Map;
import java.util.HashMap;
import java.util.Collections;
import java.util.stream.Collectors;

class LogAnalyzer {

    public Map<String, Object> analyzeLogs(List<SysLog> logs) {
        Map<String, Object> result = new HashMap<>();

        if (logs == null || logs.isEmpty()) {
            result.put("error", "No logs provided for analysis");
            return result;
        }

        result.put("totalLogs", logs.size());

        // Compute user frequency using streams
        Map<String, Long> userFrequency = logs.stream()
            .collect(Collectors.groupingBy(SysLog::getUsername, Collectors.counting()));
        result.put("userFrequency", userFrequency);

        // Find the most active user
        String mostActiveUser = Collections.max(userFrequency.entrySet(), Map.Entry.comparingByValue()).getKey();
        result.put("mostActiveUser", mostActiveUser);

        // Add time-based statistics
        long earliestLog = logs.stream().mapToLong(SysLog::getTime).min().orElse(0);
        long latestLog = logs.stream().mapToLong(SysLog::getTime).max().orElse(0);
        long timeRange = latestLog - earliestLog;

        result.put("earliestLog", earliestLog);
        result.put("latestLog", latestLog);
        result.put("timeRange", timeRange);

        // Add severity distribution
        Map<String, Long> severityDistribution = logs.stream()
            .collect(Collectors.groupingBy(SysLog::getSeverity, Collectors.counting()));
        result.put("severityDistribution", severityDistribution);

        // Find longest message
        String longestMessage = logs.stream()
            .max((log1, log2) -> Integer.compare(log1.getMessage().length(), log2.getMessage().length()))
            .map(SysLog::getMessage)
            .orElse("");
        result.put("longestMessage", longestMessage);

        return result;
    }

    public static class SysLog {
        private String username;
        private String message;
        private long time;
        private String severity;

        public SysLog(String username, String message, long time, String severity) {
            this.username = username;
            this.message = message;
            this.time = time;
            this.severity = severity;
        }

        public String getUsername() { return username; }
        public String getMessage() { return message; }
        public long getTime() { return time; }
        public String getSeverity() { return severity; }
    }
}

