    void testSeverityDistributionAndLongestMessage() {
        List<LogAnalyzer.SysLog> logs = Arrays.asList(
            new LogAnalyzer.SysLog("user1", "Short message", 1000, "INFO"),
            new LogAnalyzer.SysLog("user2", "This is a much longer message with more details", 2000, "ERROR"),
            new LogAnalyzer.SysLog("user1", "Medium length message here", 3000, "INFO"),
            new LogAnalyzer.SysLog("user3", "Warning message", 4000, "WARN")
        );

        Map<String, Object> result = analyzer.analyzeLogs(logs);

        // Check that new fields are present
        assertTrue(result.containsKey("severityDistribution"));
        assertTrue(result.containsKey("longestMessage"));

        // Check severity distribution
        @SuppressWarnings("unchecked")
        Map<String, Long> severityDist = (Map<String, Long>) result.get("severityDistribution");
        assertEquals(2, severityDist.get("INFO"));
        assertEquals(1, severityDist.get("ERROR"));
        assertEquals(1, severityDist.get("WARN"));

        // Check longest message
        String longestMsg = (String) result.get("longestMessage");
        assertEquals("This is a much longer message with more details", longestMsg);
    }

    @Test
    void testEmptyLogsStillReturnsRequiredFields() {
        List<LogAnalyzer.SysLog> logs = new ArrayList<>();
        Map<String, Object> result = analyzer.analyzeLogs(logs);

        // Should still have the error field but not the new fields
        assertTrue(result.containsKey("error"));
        assertFalse(result.containsKey("severityDistribution"));
        assertFalse(result.containsKey("longestMessage"));
    }

    @Test
    void testSeverityDistributionWithMultipleSameSeverity() {
        List<LogAnalyzer.SysLog> logs = Arrays.asList(
