import java.nio.BufferOverflowException;

class AdvancedBufferOperations {
    public String performAdvancedBufferOperations(int bufferSize, int[] writeValues) {
        StringBuilder result = new StringBuilder();
        IntBuffer intBuffer = IntBuffer.allocate(bufferSize);
        try {
            for (int value : writeValues) {
                intBuffer.put(value);
            }
        } catch (BufferOverflowException e) {
            return "Error: Attempted to write beyond buffer capacity";
        }
        intBuffer.flip();
        result.append("Initial buffer content: ");
        while (intBuffer.hasRemaining()) {
            result.append(intBuffer.get()).append(" ");
        }
        result.append("\n");

        // Reset the buffer
        intBuffer.rewind();
        // Set position to 1 and limit to 3
        intBuffer.position(1);
        intBuffer.limit(3);

        result.append("Partial buffer content (position 1, limit 3): ");
        while (intBuffer.hasRemaining()) {
            result.append(intBuffer.get()).append(" ");
        }
        result.append("\n");

        // New ByteBuffer operations
        ByteBuffer byteBuffer = ByteBuffer.allocate(16); // Enough space for int(4) + double(8) + char(2) = 14 bytes
        byteBuffer.putInt(100);
        byteBuffer.putDouble(3.14159);
        byteBuffer.putChar('A');
        byteBuffer.flip();

        result.append("Read int: ").append(byteBuffer.getInt()).append("\n");
        result.append("Read double: ").append(byteBuffer.getDouble()).append("\n");
        result.append("Read char: ").append(byteBuffer.getChar()).append("\n");

        return result.toString();
    }
}

