        categories.add(defaultCategory);
        notesMap.put(defaultCategory, new ArrayList<>());
    }

    public String getDefaultCategory() {
        return defaultCategory;
    }

    public boolean addCategory(String categoryName) {
        if (categoryName == null || categoryName.isEmpty()) {
            return false;
        }
        if (categories.add(categoryName)) {
            notesMap.put(categoryName, new ArrayList<>());
            return true;
        }
        return false;
    }

    public List<Note> removeCategory(String categoryName) {
        if (categoryName == null || !categories.contains(categoryName)) {
            return null;
        }

        categories.remove(categoryName);
        List<Note> removedNotes = notesMap.remove(categoryName);

        // If the removed category was the current one
        if (categoryName.equals(currentCategory)) {
            if (categories.isEmpty()) {
                currentCategory = "";
            } else {
                // Set current category to the first available category
                currentCategory = categories.iterator().next();
            }
        }

        return removedNotes;
    }

    public boolean addNoteToCategory(String categoryName, Note note) {
        if (categoryName == null || note == null || !categories.contains(categoryName)) {
            return false;
        }
        notesMap.get(categoryName).add(note);
        return true;
    }

    public List<Note> getNotesByCategory(String categoryName) {
        if (categoryName == null || !categories.contains(categoryName)) {
            return null;
        }
        // Return a copy of the notes list
        return new ArrayList<>(notesMap.get(categoryName));
    }

    public String getCurrentCategory() {
        return currentCategory;
    }

    public boolean setCurrentCategory(String categoryName) {
        if (categoryName == null || !categories.contains(categoryName)) {
            return false;
        }
        currentCategory = categoryName;
        return true;
    }

    public List<String> getAllCategories() {
        // Return a copy of the categories set as a list
        return new ArrayList<>(categories);
    }

    // Helper method to add notes for testing purposes
    public void addNoteHelper(String category, String title, String content) {
        if (categories.contains(category)) {
            Note note = new Note(title, content);
            notesMap.get(category).add(note);
        }
    }

    static class Note {
        private static int idCounter = 1;
        private int id;
        private String title;
        private String content;

        public Note(String title, String content) {
            this.id = idCounter++;
            this.title = title;
            this.content = content;
        }

        public int getId() {
            return id;
        }

        public String getTitle() {
            return title;
        }

        public String getContent() {
            return content;
        }
    }
}

