import java.util.*;
import java.util.regex.Pattern;

public class StringProcessor {
    public static Map<String, Object> processString(String input) {
        if (input == null) {
            throw new IllegalArgumentException("Input cannot be null");
        }

        Map<String, Object> result = new HashMap<>();
        result.put("noSpaces", input.replaceAll("\\s+", ""));
        result.put("lowercase", input.toLowerCase());
        result.put("uppercase", input.toUpperCase());
        result.put("noPunctuation", input.replaceAll("[^a-zA-Z0-9]", ""));

        // Count vowels (both uppercase and lowercase)
        int vowelCount = 0;
        String vowels = "aeiouAEIOU";
        for (int i = 0; i < input.length(); i++) {
            if (vowels.indexOf(input.charAt(i)) != -1) {
                vowelCount++;
            }
        }
        result.put("vowelCount", vowelCount);

        // Count consonants (alphabetic characters that are not vowels)
        int consonantCount = 0;
        for (int i = 0; i < input.length(); i++) {
            char c = input.charAt(i);
            if (Character.isLetter(c) && vowels.indexOf(c) == -1) {
                consonantCount++;
            }
        }
        result.put("consonantCount", consonantCount);

        // Count digits (0-9)
        int digitCount = 0;
        for (int i = 0; i < input.length(); i++) {
            if (Character.isDigit(input.charAt(i))) {
                digitCount++;
            }
        }
        result.put("digitCount", digitCount);

        // Count words (sequences separated by whitespace, ignoring leading/trailing spaces)
        String trimmedInput = input.trim();
        if (trimmedInput.isEmpty()) {
            result.put("wordCount", 0);
        } else {
            String[] words = trimmedInput.split("\\s+");
            result.put("wordCount", words.length);
        }

        return result;
    }
}

