import java.util.*;
import java.util.function.Function;

public class FunctionDemonstrator {
    public List<String> demonstrateFunctionConcepts(int baseValue) {
        int squared = baseValue * baseValue;
        List<String> result = new ArrayList<>();
        result.add("Primitive function (square) result: " + squared);

        // Add the second demonstration: doubling the input value using variable assignment
        int doubled = baseValue * 2;
        result.add("Variable assignment result: " + doubled);

        // Add the third demonstration: using method reference to private helper method
        Function<Integer, Integer> methodRef = this::addFive;
        result.add("Parameter passing result: " + methodRef.apply(baseValue));

        // Add the fourth demonstration: function composition (square then add five) via higher-order function
        Function<Integer, Integer> square = x -> x * x;
        Function<Integer, Integer> addFiveFunc = this::addFive;
        Function<Integer, Integer> composed = square.andThen(addFiveFunc);
        result.add("Higher-order function result: " + composed.apply(baseValue));

        // Add the fifth demonstration: recursive factorial calculation
        int factorialResult = factorial(baseValue);
        result.add("Recursive factorial result: " + factorialResult);

        return result;
    }

    private int addFive(int x) {
        return x + 5;
    }

    private int factorial(int n) {
        if (n <= 1) {
            return 1;
        }
        return n * factorial(n - 1);
    }
}

