import java.util.*;

public class Venue {
    private int rows;
    private int columns;
    private boolean[][] seats;

    public Venue(int rows, int columns) {
        this.rows = rows;
        this.columns = columns;
        this.seats = new boolean[rows][columns]; // false means available
    }

    public int getNumberOfSeats() {
        return rows * columns;
    }

    public boolean reserveSeat(int row, int col) {
        if (row < 1 || row > rows || col < 1 || col > columns) {
            throw new IllegalArgumentException("Invalid seat coordinates");
        }

        int rowIndex = row - 1;
        int colIndex = col - 1;

        if (seats[rowIndex][colIndex]) {
            return false; // Already reserved
        }

        seats[rowIndex][colIndex] = true;
        return true; // Successfully reserved
    }

    public int getAvailableSeats() {
        int count = 0;
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < columns; j++) {
                if (!seats[i][j]) {
                    count++;
                }
            }
        }
        return count;
    }

    public int[] getBestAvailableSeat() {
        // Iterate through rows from front to back
        for (int i = 0; i < rows; i++) {
            // Create a list of column indices sorted by distance from center
            List<Integer> sortedColumns = new ArrayList<>();
            int center = (columns - 1) / 2; // For 0-based indexing, this is the true center for both even and odd

            // Add the center column first
            sortedColumns.add(center);

            // Add columns alternating left and right from center
            for (int offset = 1; offset <= columns / 2; offset++) {
                // Add left column (center - offset) first
                if (center - offset >= 0) {
                    sortedColumns.add(center - offset);
                }
                // Add right column (center + offset)
                if (center + offset < columns) {
                    sortedColumns.add(center + offset);
                }
            }

            // Check seats in this row according to the sorted column order
            for (int colIndex : sortedColumns) {
                if (!seats[i][colIndex]) {
                    // Return 1-based indexing
                    return new int[]{i + 1, colIndex + 1};
                }
            }
        }

        // No available seats
        return null;
    }
}

