import org.junit.jupiter.api.Test;
import java.util.Arrays;

class TestCases {
    @Test
    public void testGetBestAvailableSeatFromEmptyVenue() {
        Venue venue = new Venue(3, 5);
        assertArrayEquals(new int[]{1, 3}, venue.getBestAvailableSeat());
    }

    @Test
    public void testGetBestAvailableSeatWithSomeReserved() {
        Venue venue = new Venue(3, 5);
        venue.reserveSeat(1, 3); // Reserve the best seat
        assertArrayEquals(new int[]{1, 2}, venue.getBestAvailableSeat()); // Next best in same row (left of center)
    }

    @Test
    public void testGetBestAvailableSeatFullRow() {
        Venue venue = new Venue(3, 5);
        // Reserve all seats in the first row
        for (int col = 1; col <= 5; col++) {
            venue.reserveSeat(1, col);
        }
        // Best seat should now be in the second row
        assertArrayEquals(new int[]{2, 3}, venue.getBestAvailableSeat());
    }

    @Test
    public void testGetBestAvailableSeatNoSeatsAvailable() {
        Venue venue = new Venue(1, 1);
        venue.reserveSeat(1, 1);
        assertNull(venue.getBestAvailableSeat());
    }

    @Test
    public void testGetBestAvailableSeatEvenColumns() {
        Venue venue = new Venue(2, 4);
        // For even columns, center is considered to be the left of the two middle columns (index 1)
        // So the order should be col 2 (index 1), then col 1 (index 0), then col 3 (index 2), then col 4 (index 3)
        assertArrayEquals(new int[]{1, 2}, venue.getBestAvailableSeat());
    }

    @Test
    public void testGetBestAvailableSeatOddColumns() {
        Venue venue = new Venue(2, 5);
        // For odd columns, center is the middle column (index 2)
        assertArrayEquals(new int[]{1, 3}, venue.getBestAvailableSeat());
    }
}

