import java.util.*;

class Message {
    private String content;
    private String contentType;

    public Message(String content) {
        this.content = content;
        this.contentType = "default";
    }

    public Message(String content, String contentType) {
        this.content = content;
        this.contentType = contentType;
    }

    public String getContent() {
        return content;
    }

    public String getContentType() {
        return contentType;
    }
}

enum Status {
    SUCCESS,
    FAILURE
}

class ProcessingResult {
    private Status status;
    private Map<String, Object> metadata;

    public ProcessingResult(Status status) {
        this.status = status;
        this.metadata = new HashMap<>();
    }

    public Status getStatus() {
        return status;
    }

    public Map<String, Object> getMetadata() {
        return metadata;
    }

    public void setMetadata(Map<String, Object> metadata) {
        this.metadata = metadata;
    }
}

class EnhancedMessageProcessor {
    private Map<String, List<Message>> storedMessages;
    private Map<String, Integer> messageCounts;

    public EnhancedMessageProcessor() {
        storedMessages = new LinkedHashMap<>(); // Changed to LinkedHashMap to preserve insertion order
        messageCounts = new HashMap<>();
    }

    public synchronized ProcessingResult postMessage(Message message, String destination) {
        storedMessages.computeIfAbsent(destination, k -> new ArrayList<>()).add(message);

        // Update message count for the destination
        messageCounts.put(destination, messageCounts.getOrDefault(destination, 0) + 1);

        // Create result with metadata
        ProcessingResult result = new ProcessingResult(Status.SUCCESS);
        Map<String, Object> metadata = new HashMap<>();
        metadata.put("totalMessages", messageCounts.get(destination));
        result.setMetadata(metadata);

        return result;
    }

    public synchronized Message getMessage(String destination) {
        List<Message> messages = storedMessages.get(destination);
        if (messages == null || messages.isEmpty()) {
            return null;
        }
        return messages.get(messages.size() - 1);
    }

    public synchronized Map<String, Message> getMessagesByType(String contentType) {
        Map<String, Message> result = new LinkedHashMap<>();

        for (Map.Entry<String, List<Message>> entry : storedMessages.entrySet()) {
            String destination = entry.getKey();
            List<Message> messages = entry.getValue();

            // Find the last message with the matching content type for this destination
            for (int i = messages.size() - 1; i >= 0; i--) {
                Message message = messages.get(i);
                if (contentType.equals(message.getContentType())) {
                    result.put(destination, message);
                    break; // Only take the last matching message per destination
                }
            }
        }

        return result;
    }

    public synchronized Status clearMessages(String destination) {
        if (storedMessages.containsKey(destination)) {
            storedMessages.remove(destination);
            messageCounts.remove(destination);
            return Status.SUCCESS;
        } else {
            return Status.FAILURE;
        }
    }
}

