        processor.postMessage(new Message("text1", "text"), "dest1");
        processor.postMessage(new Message("image1", "image"), "dest1");
        processor.postMessage(new Message("text2", "text"), "dest2");
        processor.postMessage(new Message("audio1", "audio"), "dest2");
        processor.postMessage(new Message("text3", "text"), "dest3");

        // Get messages with content type "text"
        Map<String, Message> textMessages = processor.getMessagesByType("text");

        // Verify the map preserves insertion order and contains correct messages
        assertEquals(3, textMessages.size());
        assertTrue(textMessages.containsKey("dest1"));
        assertEquals("text1", textMessages.get("dest1").getContent());
        assertTrue(textMessages.containsKey("dest2"));
        assertEquals("text2", textMessages.get("dest2").getContent());
        assertTrue(textMessages.containsKey("dest3"));
        assertEquals("text3", textMessages.get("dest3").getContent());

        // Verify order is preserved (dest1, dest2, dest3)
        Iterator<Map.Entry<String, Message>> iterator = textMessages.entrySet().iterator();
        assertEquals("dest1", iterator.next().getKey());
        assertEquals("dest2", iterator.next().getKey());
        assertEquals("dest3", iterator.next().getKey());
