        public int totalCount;
        public int validCount;
        public int secureCount;
        public double averageLength;
        public String longestBookmark;
        public String shortestBookmark;

        public BookmarkAnalysisResult(int totalCount, int validCount, int secureCount, double averageLength, String longestBookmark, String shortestBookmark) {
