        assertTrue(util.isLeapYear(2000));
        assertTrue(util.isLeapYear(2400));
    }

    @Test
    void testNotLeapYearDivisibleBy100Not400() {
        DateUtil util = new DateUtil();
        assertFalse(util.isLeapYear(1900));
        assertFalse(util.isLeapYear(2100));
        assertFalse(util.isLeapYear(2200));
        assertFalse(util.isLeapYear(2300));
    }

    @Test
    void testLeapYearDivisibleBy4Not100() {
        DateUtil util = new DateUtil();
        assertTrue(util.isLeapYear(2024));
        assertTrue(util.isLeapYear(2028));
        assertTrue(util.isLeapYear(2004));
    }

    @Test
