        DateUtil util = new DateUtil();
        assertFalse(util.isLeapYear(2023));
        assertFalse(util.isLeapYear(2025));
        assertFalse(util.isLeapYear(2026));
    }

    @Test
    void testLeapYearEdgeCases() {
        DateUtil util = new DateUtil();
        assertTrue(util.isLeapYear(0)); // Year 0 is divisible by 400
        assertFalse(util.isLeapYear(100)); // Divisible by 100 but not 400
        assertTrue(util.isLeapYear(400)); // Divisible by 400
    }
}

