class TestCases {
    private FileOperationsManager manager;
    private String testFilePath;

    @BeforeEach
    void setUp() {
        manager = new FileOperationsManager();
        testFilePath = "test_file.txt";
    }

    @AfterEach
    void tearDown() {
        File file = new File(testFilePath);
        if (file.exists()) {
            file.delete();
        }
    }

    @Test
    void testFileExistsWhenFileExistsAndReadable() throws IOException {
        // Create a file
        File file = new File(testFilePath);
        file.createNewFile();

        // Test that fileExists returns true
        assertTrue(manager.fileExists(testFilePath));
    }

    @Test
    void testFileExistsWhenFileDoesNotExist() {
        // Test that fileExists returns false for non-existent file
        assertFalse(manager.fileExists("non_existent_file.txt"));
    }

    @Test
    void testFileExistsWhenFileExistsButNotReadable() throws IOException {
        // Create a file
        File file = new File(testFilePath);
        file.createNewFile();

        // Store original readability state
        boolean wasReadable = file.canRead();

        try {
            // Try to make file not readable
            if (file.setReadable(false)) {
                // Test that fileExists returns false
                assertFalse(manager.fileExists(testFilePath), "File should not be considered existing if not readable");
            } else {
                // If we can't change permissions, skip this test gracefully
                // This can happen on some operating systems
                assertTrue(true); // Test passes by default if permission change not supported
            }
        } finally {
            // Restore readability for cleanup
            if (wasReadable) {
                file.setReadable(true);
            }
        }
    }
}

