import java.lang.reflect.Array;
import java.util.*;

public class ObjectInspector {
    public Map<String, Object> inspectObject(Object obj) {
        return inspectObject(obj, Integer.MAX_VALUE);
    }

    public Map<String, Object> inspectObject(Object obj, int maxDepth) {
        Map<String, Object> result = new HashMap<>();

        if (obj == null) {
            result.put("value", null);
        } else if (obj.getClass().isArray()) {
            if (maxDepth <= 0) {
                result.put("type", obj.getClass().getSimpleName());
                result.put("value", "<max depth reached>");
            } else {
                result.put("type", obj.getClass().getComponentType().getSimpleName() + "[]");
                int length = Array.getLength(obj);
                List<Map<String, Object>> elements = new ArrayList<>();
                for (int i = 0; i < length; i++) {
                    Object element = Array.get(obj, i);
                    elements.add(inspectObject(element, maxDepth - 1));
                }
                result.put("value", elements);
            }
        } else {
            result.put("type", obj.getClass().getSimpleName());

            if (isSimpleType(obj)) {
                result.put("value", obj);
            }
        }

        return result;
    }

    private boolean isSimpleType(Object obj) {
        return obj instanceof String ||
               obj instanceof Number ||
               obj instanceof Boolean ||
               obj instanceof Character;
    }
}

