import java.util.*;

class TestCases {
    static class Person {
        private String name;
        private int age;
        private Address address;

        public Person(String name, int age, Address address) {
            this.name = name;
            this.age = age;
            this.address = address;
        }
    }

    static class Address {
        private String street;
        private String city;

        public Address(String street, String city) {
            this.street = street;
            this.city = city;
        }
    }

    static class EmptyClass {}

    static class InheritedFields extends Person {
        private String job;

        public InheritedFields(String name, int age, Address address, String job) {
            super(name, age, address);
            this.job = job;
        }
    }

    @Test
    public void testComplexObjectWithFields() {
        Address address = new Address("123 Main St", "Springfield");
        Person person = new Person("John Doe", 30, address);

        ObjectInspector inspector = new ObjectInspector();
        Map<String, Object> result = inspector.inspectObject(person);

        assertEquals("Person", result.get("type"));

        @SuppressWarnings("unchecked")
        Map<String, Object> fields = (Map<String, Object>) result.get("fields");
        assertNotNull(fields);

        assertEquals(3, fields.size());

        @SuppressWarnings("unchecked")
        Map<String, Object> nameResult = (Map<String, Object>) fields.get("name");
        assertEquals("John Doe", nameResult.get("value"));

        @SuppressWarnings("unchecked")
        Map<String, Object> ageResult = (Map<String, Object>) fields.get("age");
        assertEquals(30, ageResult.get("value"));

        @SuppressWarnings("unchecked")
        Map<String, Object> addressResult = (Map<String, Object>) fields.get("address");
        assertEquals("Address", addressResult.get("type"));

        @SuppressWarnings("unchecked")
        Map<String, Object> addressFields = (Map<String, Object>) addressResult.get("fields");
        assertNotNull(addressFields);

        @SuppressWarnings("unchecked")
        Map<String, Object> streetResult = (Map<String, Object>) addressFields.get("street");
        assertEquals("123 Main St", streetResult.get("value"));

        @SuppressWarnings("unchecked")
        Map<String, Object> cityResult = (Map<String, Object>) addressFields.get("city");
        assertEquals("Springfield", cityResult.get("value"));
    }

    @Test
    public void testInheritedFields() {
        Address address = new Address("456 Oak Ave", "Shelbyville");
        InheritedFields person = new InheritedFields("Jane Smith", 25, address, "Engineer");

        ObjectInspector inspector = new ObjectInspector();
        Map<String, Object> result = inspector.inspectObject(person);

        assertEquals("InheritedFields", result.get("type"));

        @SuppressWarnings("unchecked")
        Map<String, Object> fields = (Map<String, Object>) result.get("fields");
        assertNotNull(fields);

        // Should include both inherited and own fields
        assertTrue(fields.containsKey("name"));
        assertTrue(fields.containsKey("age"));
        assertTrue(fields.containsKey("address"));
        assertTrue(fields.containsKey("job"));

        @SuppressWarnings("unchecked")
        Map<String, Object> jobResult = (Map<String, Object>) fields.get("job");
        assertEquals("Engineer", jobResult.get("value"));
    }

    @Test
    public void testEmptyClass() {
        EmptyClass obj = new EmptyClass();

        ObjectInspector inspector = new ObjectInspector();
        Map<String, Object> result = inspector.inspectObject(obj);

        assertEquals("EmptyClass", result.get("type"));

        @SuppressWarnings("unchecked")
        Map<String, Object> fields = (Map<String, Object>) result.get("fields");
        assertNotNull(fields);
        assertEquals(0, fields.size());
    }

    @Test
    public void testMaxDepthLimit() {
        Address address = new Address("789 Pine St", "Ogdenville");
        Person person = new Person("Max Depth", 40, address);

        ObjectInspector inspector = new ObjectInspector();
        Map<String, Object> result = inspector.inspectObject(person, 1);

        assertEquals("Person", result.get("type"));

        @SuppressWarnings("unchecked")
        Map<String, Object> fields = (Map<String, Object>) result.get("fields");
        assertNotNull(fields);

        // At depth 1, the address field should show "<max depth reached>"
        @SuppressWarnings("unchecked")
        Map<String, Object> addressResult = (Map<String, Object>) fields.get("address");
        assertEquals("Address", addressResult.get("type"));
        assertEquals("<max depth reached>", addressResult.get("value"));
    }
}

