import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.lang.reflect.Field;
import java.util.*;

class TestCases extends junit.framework.TestCase {

    static class Parent {
        private String parentField = "parentValue";
        private static String staticParentField = "staticParentValue";
    }

    static class Child extends Parent {
        private int childField = 42;
        private static int staticChildField = 100;
    }

    @Test
    public void testGetAllFields() {
        ObjectInspector inspector = new ObjectInspector();
        List<Field> fields = inspector.getAllFields(Child.class);

        // Should include fields from both Child and Parent classes
        assertEquals(2, fields.size()); // childField and parentField

        boolean hasChildField = false;
        boolean hasParentField = false;

        for (Field field : fields) {
            if ("childField".equals(field.getName())) {
                hasChildField = true;
            }
            if ("parentField".equals(field.getName())) {
                hasParentField = true;
            }
            // Should not include static fields
            assertFalse(java.lang.reflect.Modifier.isStatic(field.getModifiers()));
        }

        assertTrue(hasChildField);
        assertTrue(hasParentField);
    }

    @Test
    public void testCreateSimpleFieldMapWithSimpleType() {
        ObjectInspector inspector = new ObjectInspector();

        // Test with String
        Map<String, Object> result = inspector.createSimpleFieldMap("test");
        assertEquals("String", result.get("type"));
        assertEquals("test", result.get("value"));

        // Test with Integer
        result = inspector.createSimpleFieldMap(123);
        assertEquals("Integer", result.get("type"));
        assertEquals(123, result.get("value"));

        // Test with Boolean
        result = inspector.createSimpleFieldMap(true);
        assertEquals("Boolean", result.get("type"));
        assertEquals(true, result.get("value"));
    }

    @Test
    public void testCreateSimpleFieldMapWithNull() {
        ObjectInspector inspector = new ObjectInspector();
        Map<String, Object> result = inspector.createSimpleFieldMap(null);
        assertNull(result.get("value"));
        // For null, we only put "value" in the map
        assertEquals(1, result.size());
    }

    @Test
    public void testCreateSimpleFieldMapWithComplexType() {
        ObjectInspector inspector = new ObjectInspector();
        Map<String, Object> result = inspector.createSimpleFieldMap(new Child());
        // For complex objects, should return an empty map
        assertTrue(result.isEmpty());
    }

    @Test
    public void testInspectObjectSkipsStaticFields() {
        ObjectInspector inspector = new ObjectInspector();
        Child child = new Child();
        Map<String, Object> result = inspector.inspectObject(child);

        @SuppressWarnings("unchecked")
        Map<String, Object> fields = (Map<String, Object>) result.get("fields");

        // Should contain instance fields
        assertTrue(fields.containsKey("childField"));
        assertTrue(fields.containsKey("parentField"));

        // Should not contain static fields
        assertFalse(fields.containsKey("staticChildField"));
        assertFalse(fields.containsKey("staticParentField"));
    }
}

