import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.HashMap;
import java.util.Map;

class TestCases {
    @Test
    void testEnhancedApiErrorWithAllFields() {
        ErrorCode errorCode = ErrorCode.INVALID_CREDENTIALS;
        ErrorResponse errorResponse = new ErrorResponse(401, "Unauthorized");
        Map<String, String> details = new HashMap<>();
        details.put("timestamp", "2023-01-01T00:00:00Z");
        details.put("endpoint", "/api/login");

        EnhancedApiError error = new EnhancedApiError(errorCode, errorResponse, details);

        assertEquals(errorCode, error.getErrorCode());
        assertEquals(errorResponse, error.getOriginalResponse());
        assertEquals(401, error.getOriginalResponse().getStatusCode());
        assertEquals("Unauthorized", error.getOriginalResponse().getRawResponse());

        Map<String, String> returnedDetails = error.getAdditionalDetails();
        assertEquals(2, returnedDetails.size());
        assertEquals("2023-01-01T00:00:00Z", returnedDetails.get("timestamp"));
        assertEquals("/api/login", returnedDetails.get("endpoint"));
    }

    @Test
    void testDefensiveCopyOfAdditionalDetails() {
        ErrorCode errorCode = ErrorCode.RATE_LIMIT_EXCEEDED;
        ErrorResponse errorResponse = new ErrorResponse(429, "Too Many Requests");
        Map<String, String> originalDetails = new HashMap<>();
        originalDetails.put("limit", "100");

        EnhancedApiError error = new EnhancedApiError(errorCode, errorResponse, originalDetails);

        // Modify the original map after creating the error
        originalDetails.put("newKey", "newValue");

        // The error's details should not be affected
        Map<String, String> returnedDetails = error.getAdditionalDetails();
        assertEquals(1, returnedDetails.size());
        assertEquals("100", returnedDetails.get("limit"));
        assertFalse(returnedDetails.containsKey("newKey"));

        // Modify the returned map
        returnedDetails.put("anotherKey", "anotherValue");

        // Get details again - should still only have the original entry
        Map<String, String> returnedDetails2 = error.getAdditionalDetails();
        assertEquals(1, returnedDetails2.size());
        assertEquals("100", returnedDetails2.get("limit"));
        assertFalse(returnedDetails2.containsKey("anotherKey"));
    }

    @Test
    void testNullAdditionalDetails() {
        ErrorCode errorCode = ErrorCode.SESSION_EXPIRED;
        ErrorResponse errorResponse = new ErrorResponse(403, "Forbidden");

        EnhancedApiError error = new EnhancedApiError(errorCode, errorResponse, null);

        assertEquals(errorCode, error.getErrorCode());
        assertEquals(errorResponse, error.getOriginalResponse());
        assertNotNull(error.getAdditionalDetails());
        assertTrue(error.getAdditionalDetails().isEmpty());
    }
}

