import java.util.Map;
import java.util.HashMap;

class ErrorResponse {
    private int statusCode;
    private String rawResponse;

    public ErrorResponse(int statusCode, String rawResponse) {
        this.statusCode = statusCode;
        this.rawResponse = rawResponse;
    }

    public int getStatusCode() {
        return statusCode;
    }

    public String getRawResponse() {
        return rawResponse;
    }
}

public class EnhancedApiError extends Exception {
    private ErrorCode errorCode;
    private ErrorResponse errorResponse;
    private Map<String, String> additionalDetails;

    public EnhancedApiError(ErrorCode errorCode, ErrorResponse errorResponse, Map<String, String> additionalDetails) {
        this.errorCode = errorCode;
        this.errorResponse = errorResponse;
        // Defensive copy of the map
        this.additionalDetails = additionalDetails != null ? new HashMap<>(additionalDetails) : new HashMap<>();
    }

    public ErrorCode getErrorCode() {
        return errorCode;
    }

    public ErrorResponse getOriginalResponse() {
        return errorResponse;
    }

    public Map<String, String> getAdditionalDetails() {
        // Return a defensive copy
        return new HashMap<>(additionalDetails);
    }

    public String getBaseMessage() {
        return ErrorMessageResolver.resolve(this.errorCode);
    }

    public String getEnhancedMessage() {
        StringBuilder message = new StringBuilder(getBaseMessage());
        if (!additionalDetails.isEmpty()) {
            message.append("\nAdditional Details:");
            for (Map.Entry<String, String> entry : additionalDetails.entrySet()) {
                message.append("\n- ").append(entry.getKey()).append(": ").append(entry.getValue());
            }
        }
        return message.toString();
    }

    public boolean isRecoverable() {
        return this.errorCode.getSeverity() != ErrorSeverity.CRITICAL;
    }
}

enum ErrorCode {
    NON_KEY_ACCOUNT_BALANCE_ERROR(ErrorSeverity.HIGH, "Non-key account balance error occurred"),
