import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.HashMap;
import java.util.Map;

class TestCases {
    @Test
    void testGetBaseMessage() {
        ErrorCode errorCode = ErrorCode.SESSION_EXPIRED;
        ErrorResponse response = new ErrorResponse(401, "Session expired response");
        Map<String, String> details = new HashMap<>();
        details.put("userId", "12345");
        EnhancedApiError error = new EnhancedApiError(errorCode, response, details);
        assertEquals("Session has expired", error.getBaseMessage());
    }

    @Test
    void testGetEnhancedMessageWithDetails() {
        ErrorCode errorCode = ErrorCode.RATE_LIMIT_EXCEEDED;
        ErrorResponse response = new ErrorResponse(429, "Rate limit response");
        Map<String, String> details = new HashMap<>();
        details.put("limit", "1000 requests/hour");
        details.put("resetTime", "2023-05-10T12:00:00Z");
        EnhancedApiError error = new EnhancedApiError(errorCode, response, details);
        String expected = "Rate limit exceeded\nAdditional Details:\n- limit: 1000 requests/hour\n- resetTime: 2023-05-10T12:00:00Z";
        assertEquals(expected, error.getEnhancedMessage());
    }

    @Test
    void testGetEnhancedMessageWithoutDetails() {
        ErrorCode errorCode = ErrorCode.NON_KEY_ACCOUNT_BALANCE_ERROR;
        ErrorResponse response = new ErrorResponse(500, "Balance error response");
        Map<String, String> details = new HashMap<>();
        EnhancedApiError error = new EnhancedApiError(errorCode, response, details);
        assertEquals("Non-key account balance error occurred", error.getEnhancedMessage());
    }

    @Test
    void testIsRecoverableTrue() {
        ErrorCode errorCode = ErrorCode.SESSION_EXPIRED;
        ErrorResponse response = new ErrorResponse(401, "Session expired response");
        Map<String, String> details = new HashMap<>();
        EnhancedApiError error = new EnhancedApiError(errorCode, response, details);
        assertTrue(error.isRecoverable());
    }

    @Test
    void testIsRecoverableFalse() {
        ErrorCode errorCode = ErrorCode.INVALID_CREDENTIALS;
        ErrorResponse response = new ErrorResponse(403, "Invalid credentials response");
        Map<String, String> details = new HashMap<>();
        EnhancedApiError error = new EnhancedApiError(errorCode, response, details);
        assertFalse(error.isRecoverable());
    }
}

