    // Hardcoded database of phone numbers
    private Set<String> validPhoneNumbers;
    private Map<String, String> verificationCodes; // Store verification codes per user

    public UserAuthenticator() {
        validPhoneNumbers = new HashSet<>();
        validPhoneNumbers.add("1234567890");
        validPhoneNumbers.add("0987654321");
        verificationCodes = new HashMap<>();
    }

    public AuthenticationResult authenticate(String phoneNumber, String password) {
        // Input validation
        if (phoneNumber == null || phoneNumber.isEmpty()) {
            throw new IllegalArgumentException("Phone number cannot be null or empty");
        }

        if (password == null || password.isEmpty()) {
            throw new IllegalArgumentException("Password cannot be null or empty");
        }

        if (phoneNumber.length() != 10) {
            throw new IllegalArgumentException("Phone number must be exactly 10 digits");
        }

        // Check if the phone number exists in the database
        if (validPhoneNumbers.contains(phoneNumber)) {
            // For this basic implementation, we assume any non-null password is valid
            // In a real system, you would check the password against a stored hash
            String code = generateVerificationCode();
            verificationCodes.put(phoneNumber, code);
            return new AuthenticationResult(250, "Verification required", phoneNumber);
        } else {
            return new AuthenticationResult(404, "User not found", null);
        }
    }

    // New method to generate a verification code
    public String generateVerificationCode() {
        return "123456";
    }

    // New method to verify the code
    public AuthenticationResult verifyCode(String phoneNumber, String code) {
        String storedCode = verificationCodes.get(phoneNumber);
        if (storedCode != null && storedCode.equals(code)) {
            verificationCodes.remove(phoneNumber); // Clear the code after successful verification
            return new AuthenticationResult(200, "Verification successful", phoneNumber);
        } else {
            return new AuthenticationResult(401, "Invalid verification code", null);
        }
    }

    public static class AuthenticationResult {
        public final int statusCode;
        public final String message;
        public final String user;

