import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.concurrent.*;
import java.util.concurrent.atomic.AtomicInteger;

class TestCases {

    @Test
    void testThreadSafetyWithConcurrentHashMap() throws InterruptedException {
        final P2PAddressServer server = new P2PAddressServer(100);
        final int numThreads = 10;
        final int registrationsPerThread = 10;
        final ExecutorService executor = Executors.newFixedThreadPool(numThreads);
        final CountDownLatch latch = new CountDownLatch(numThreads);

        // Submit multiple threads to register peers concurrently
        for (int i = 0; i < numThreads; i++) {
            final int threadId = i;
            executor.submit(() -> {
                try {
                    for (int j = 0; j < registrationsPerThread; j++) {
                        String peerId = "peer-" + threadId + "-" + j;
                        String address = "192.168.1." + (threadId * 10 + j);
                        int port = 8000 + threadId * 100 + j;
                        server.registerPeer(peerId, address, port);
                    }
                } finally {
                    latch.countDown();
                }
            });
        }

        // Wait for all threads to complete
        latch.await();
        executor.shutdown();

        // Verify that all registrations were successful by checking that no registration failed due to duplicate IDs
        // Since all peer IDs are unique, all should succeed if the implementation is thread-safe
        // We can verify this by ensuring no exceptions were thrown and all operations completed
        // As we can't directly access the registry size, we'll verify by attempting to register duplicates
        boolean allUnique = true;
        for (int i = 0; i < numThreads; i++) {
            for (int j = 0; j < registrationsPerThread; j++) {
                String peerId = "peer-" + i + "-" + j;
                // Try to register the same peer again - should fail
                if (server.registerPeer(peerId, "192.168.1.1", 9000)) {
                    allUnique = false;
                    break;
                }
            }
            if (!allUnique) break;
        }

        assertTrue(allUnique, "All peer registrations should be unique and successful");
    }

    @Test
    void testConcurrentRegistrationOfSamePeerId() throws InterruptedException {
        final P2PAddressServer server = new P2PAddressServer(50);
        final String peerId = "duplicate-peer";
        final int numThreads = 5;
        final ExecutorService executor = Executors.newFixedThreadPool(numThreads);
        final CountDownLatch latch = new CountDownLatch(numThreads);
        final AtomicInteger successCount = new AtomicInteger(0);

        // Try to register the same peer ID from multiple threads
        for (int i = 0; i < numThreads; i++) {
            final int port = 9000 + i; // Make port effectively final
            executor.submit(() -> {
                try {
                    boolean result = server.registerPeer(peerId, "127.0.0.1", port);
                    if (result) {
                        successCount.incrementAndGet();
                    }
                } finally {
                    latch.countDown();
                }
            });
        }

        // Wait for all threads to complete
        latch.await();
        executor.shutdown();

        // Only one registration should succeed
        assertEquals(1, successCount.get(), "Only one thread should have successfully registered the peer");
    }
}

