import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;
import java.util.Map;

class TestCases {
    @Test
    public void testUnregisterPeer() {
        P2PAddressServer server = new P2PAddressServer();
        server.registerPeer("peer1", "192.168.1.1", 8080);

        // Test successful unregistration
        assertTrue(server.unregisterPeer("peer1"));

        // Test unregistration of non-existent peer
        assertFalse(server.unregisterPeer("peer2"));

        // Test unregistration with null peerId
        assertFalse(server.unregisterPeer(null));
    }

    @Test
    public void testGetPeerInfo() {
        P2PAddressServer server = new P2PAddressServer();
        server.registerPeer("peer1", "192.168.1.1", 8080);

        // Test getting existing peer info
        P2PAddressServer.PeerInfo info = server.getPeerInfo("peer1");
        assertNotNull(info);
        assertEquals("192.168.1.1", info.getAddress());
        assertEquals(8080, info.getPort());

        // Test getting non-existent peer info
        assertNull(server.getPeerInfo("peer2"));

        // Test getting peer info with null peerId
        assertNull(server.getPeerInfo(null));
    }

    @Test
    public void testGetAllPeers() {
        P2PAddressServer server = new P2PAddressServer();
        server.registerPeer("peer1", "192.168.1.1", 8080);
        server.registerPeer("peer2", "192.168.1.2", 8081);

        // Test getting all peers
        Map<String, P2PAddressServer.PeerInfo> allPeers = server.getAllPeers();
        assertEquals(2, allPeers.size());
        assertTrue(allPeers.containsKey("peer1"));
        assertTrue(allPeers.containsKey("peer2"));

        // Test that returned map is a snapshot (modifications don't affect server)
        allPeers.remove("peer1");
        assertEquals(2, server.getPeerCount());
        assertNotNull(server.getPeerInfo("peer1"));
    }

    @Test
    public void testGetPeerCount() {
        P2PAddressServer server = new P2PAddressServer();
        assertEquals(0, server.getPeerCount());

        server.registerPeer("peer1", "192.168.1.1", 8080);
        assertEquals(1, server.getPeerCount());

        server.registerPeer("peer2", "192.168.1.2", 8081);
        assertEquals(2, server.getPeerCount());

        server.unregisterPeer("peer1");
        assertEquals(1, server.getPeerCount());
    }
}

