import java.util.ArrayList;
import java.util.Collections;

class CargoOptimizer {

    public static int calculateMaxTrips(ArrayList<Integer> weights, int capacity) {
        // Create a copy to avoid modifying the original list
        ArrayList<Integer> items = new ArrayList<>(weights);

        int trips = 0;

        // First, handle items that can travel alone (weight >= capacity)
        ArrayList<Integer> remainingItems = new ArrayList<>();
        for (int weight : items) {
            if (weight >= capacity) {
                trips++;
            } else {
                remainingItems.add(weight);
            }
        }

        // Sort remaining items in descending order for greedy approach
        Collections.sort(remainingItems, Collections.reverseOrder());

        // Process groups from the remaining items
        while (!remainingItems.isEmpty()) {
            int heaviest = remainingItems.get(0);

            // Find the smallest group size that satisfies the condition
            int groupSize = 1;
            while (groupSize <= remainingItems.size() && heaviest * groupSize < capacity) {
                groupSize++;
            }

            // If we found a valid group size
            if (groupSize <= remainingItems.size()) {
                // Remove the group items
                for (int j = 0; j < groupSize; j++) {
                    remainingItems.remove(0);
                }
                trips++;
            } else {
                // If no valid group was formed, send the heaviest item alone
                remainingItems.remove(0);
                trips++;
            }
        }

        return trips;
    }
}

