class TestCases {
    @Test
    public void testOptimizedGrouping() {
        // Test case where the previous algorithm would be inefficient
        ArrayList<Integer> weights = new ArrayList<>(Arrays.asList(10, 10, 10, 7, 7, 7, 7, 7));
        int capacity = 20;
        // Previous algorithm would form groups like (10,7), (10,7), (10,7), (7,7) = 4 trips
        // Optimized algorithm should form groups like (10,10), (10,7,7), (7,7,7), (7) = 4 trips
        // But actually, it could be (10,10), (10,7,7), (7,7,7), (7) = 4 trips
        // Or even better: (10,10), (10,7,7), (7,7,7), (7) = 4 trips
        // Let's use a clearer example:
        // weights = [15, 10, 10, 5, 5, 5] capacity = 20
        // Previous: (15,5), (10,10), (5,5) = 3 trips
        // Optimized: (15,5), (10,10), (5) = 3 trips (same)
        // Let's try: weights = [12, 10, 9, 8, 7, 6, 5] capacity = 20
        // Previous might do: (12,8), (10,9), (7,6,5) = 3 trips
        // Optimized should do the same or better
        ArrayList<Integer> weights2 = new ArrayList<>(Arrays.asList(12, 10, 9, 8, 7, 6, 5));
        assertEquals(3, CargoOptimizer.calculateMaxTrips(weights2, 20));

        // Another test case
        ArrayList<Integer> weights3 = new ArrayList<>(Arrays.asList(15, 10, 10, 5, 5, 5));
        assertEquals(3, CargoOptimizer.calculateMaxTrips(weights3, 20));
    }

