import java.util.*;
import java.lang.reflect.*;

public class PaginationService<T> {
    public static class PaginatedResult<T> {
        private List<T> items;
        private int total;

        public PaginatedResult(List<T> items, int total) {
            this.items = items;
            this.total = total;
        }

        public List<T> getItems() {
            return items;
        }

        public int getTotal() {
            return total;
        }
    }

    private List<T> filterItems(List<T> items, String key) {
        if (key == null || key.isEmpty()) {
            return items;
        }

        List<T> filtered = new ArrayList<>();
        String lowerKey = key.toLowerCase();

        for (T item : items) {
            if (item != null && item.toString().toLowerCase().contains(lowerKey)) {
                filtered.add(item);
            }
        }

        return filtered;
    }

    private List<T> sortItems(List<T> items, String sortBy) {
        if (sortBy == null || sortBy.isEmpty() || items.isEmpty()) {
            return items;
        }

        boolean descending = false;
        String fieldName = sortBy;

        if (sortBy.startsWith("-")) {
            descending = true;
            fieldName = sortBy.substring(1);
        }

        try {
            final String finalFieldName = fieldName;
            final boolean finalDescending = descending;

            Collections.sort(items, new Comparator<T>() {
                @Override
                public int compare(T o1, T o2) {
                    try {
                        Field field = o1.getClass().getDeclaredField(finalFieldName);
                        field.setAccessible(true);

                        Object value1 = field.get(o1);
                        Object value2 = field.get(o2);

                        if (value1 == null && value2 == null) return 0;
                        if (value1 == null) return finalDescending ? 1 : -1;
                        if (value2 == null) return finalDescending ? -1 : 1;

                        if (value1 instanceof Comparable && value2 instanceof Comparable) {
                            int result = ((Comparable) value1).compareTo((Comparable) value2);
                            return finalDescending ? -result : result;
                        }

                        return 0;
                    } catch (Exception e) {
                        return 0;
                    }
                }
            });
        } catch (Exception e) {
            // If field doesn't exist or is inaccessible, return unsorted list
            return items;
        }

        return items;
    }

    public PaginatedResult<T> paginate(List<T> items, int page, int rowsPerPage, String key, String sortBy) {
        if (items == null) {
            items = new ArrayList<>();
        }

        // Apply filtering if key is provided
        List<T> filteredItems = filterItems(items, key);

        // Apply sorting if sortBy is provided
        List<T> sortedItems = sortItems(filteredItems, sortBy);

        int total = sortedItems.size();

        // Handle invalid page or rowsPerPage
        if (page < 1 || rowsPerPage < 1) {
            return new PaginatedResult<>(new ArrayList<>(), total);
        }

        int startIndex = (page - 1) * rowsPerPage;
        int endIndex = Math.min(startIndex + rowsPerPage, total);

        // If start index is beyond the list size, return empty list
        if (startIndex >= total) {
            return new PaginatedResult<>(new ArrayList<>(), total);
        }

        List<T> pageItems = new ArrayList<>(sortedItems.subList(startIndex, endIndex));
        return new PaginatedResult<>(pageItems, total);
    }

    public PaginatedResult<T> paginate(List<T> items, int page, int rowsPerPage, String key) {
        return paginate(items, page, rowsPerPage, key, null);
    }

    // Overloaded method for backward compatibility
    public PaginatedResult<T> paginate(List<T> items, int page, int rowsPerPage) {
        return paginate(items, page, rowsPerPage, null, null);
    }
}

