import java.util.*;

class AccidentNatureInfo {
    private String natureCode;
    private String description;
    private int severityLevel;

    // Static map for predefined accident codes and descriptions
    private static final Map<String, String> NATURE_DESCRIPTIONS = new HashMap<>();

    static {
        NATURE_DESCRIPTIONS.put("FIRE", "Fire-related accident");
        NATURE_DESCRIPTIONS.put("CHEM", "Chemical spill or exposure");
        NATURE_DESCRIPTIONS.put("MECH", "Mechanical failure or malfunction");
        NATURE_DESCRIPTIONS.put("ELEC", "Electrical fault or shock");
        NATURE_DESCRIPTIONS.put("FALL", "Fall from height or slip");
    }

    // Default constructor
    public AccidentNatureInfo() {
        this.natureCode = "UNKN";
        this.description = "Unknown nature";
        this.severityLevel = 1;
    }

    // Parameterized constructor
    public AccidentNatureInfo(String natureCode, String description, int severityLevel) {
        if (natureCode == null || natureCode.isEmpty()) {
            throw new IllegalArgumentException("natureCode cannot be null or empty");
        }
        this.natureCode = natureCode.toUpperCase();
        this.description = description;
        if (severityLevel < 1 || severityLevel > 5) {
            throw new IllegalArgumentException("severityLevel must be between 1 and 5");
        }
        this.severityLevel = severityLevel;
    }

    // Getter methods
    public String getNatureCode() {
        return natureCode;
    }

    public String getDescription() {
        // Return predefined description if natureCode exists in map, otherwise return stored description
        if (NATURE_DESCRIPTIONS.containsKey(natureCode)) {
            return NATURE_DESCRIPTIONS.get(natureCode);
        }
        return description;
    }

    public int getSeverityLevel() {
        return severityLevel;
    }
}

