import java.util.*;
import java.util.concurrent.*;

public class User {
    private String name;

    // Thread-safe caches
    private static final ConcurrentHashMap<String, User> accountCache = new ConcurrentHashMap<>();
    private static final ConcurrentHashMap<String, User> emailCache = new ConcurrentHashMap<>();
    private static final ConcurrentHashMap<Long, User> idCache = new ConcurrentHashMap<>();

    public User(String name) {
        this.name = name;
    }

    public String getName() {
        return name;
    }

    public static Map<String, Object> authenticateUser(Object identifier, String password) {
        Map<String, Object> result = new HashMap<>();

        // Check if identifier is of valid type
        if (!(identifier instanceof String) && !(identifier instanceof Long)) {
            throw new IllegalArgumentException("Identifier must be String or Long");
        }

        User user = null;

        // Handle Long identifier
        if (identifier instanceof Long) {
            Long id = (Long) identifier;
            // Check cache first
            user = idCache.get(id);
            if (user == null) {
                // Simulate database lookup
                user = findUserInDatabase(id);
                if (user != null) {
                    cacheUser(user, id);
                }
            }

            if (user != null && "password".equals(password)) {
                result.put("authenticated", true);
                result.put("user", user);
            } else {
                result.put("authenticated", false);
                result.put("error", "Invalid credentials");
            }
            return result;
        }

        // Handle String identifier
        String strIdentifier = (String) identifier;
        boolean isEmail = strIdentifier.contains("@");

        // Check cache first
        if (isEmail) {
            user = emailCache.get(strIdentifier);
        } else {
            user = accountCache.get(strIdentifier);
        }

        if (user == null) {
            // Simulate database lookup
            user = findUserInDatabase(strIdentifier);
            if (user != null) {
                cacheUser(user, strIdentifier);
            }
        }

        if (user != null && "password".equals(password)) {
            result.put("authenticated", true);
            result.put("user", user);
        } else {
            result.put("authenticated", false);
            result.put("error", "Invalid credentials");
        }

        return result;
    }

    // Simulate database lookup for Long identifier
    private static User findUserInDatabase(Long id) {
        if (id == 1L) {
            return new User("testuser");
        }
        return null;
    }

    // Simulate database lookup for String identifier
    private static User findUserInDatabase(String identifier) {
        boolean isEmail = identifier.contains("@");
        String expectedIdentifier = isEmail ? "test@example.com" : "testuser";

        if (expectedIdentifier.equals(identifier)) {
            return new User("testuser");
        }
        return null;
    }

    // Cache user in all caches
    private static void cacheUser(User user, Object identifier) {
        // Always cache in account cache
        accountCache.put(user.getName(), user);

        // Also cache with the original identifier if it's different
        if (identifier instanceof String) {
            String strIdentifier = (String) identifier;
            if (strIdentifier.contains("@")) {
                emailCache.put(strIdentifier, user);
            } else {
                accountCache.put(strIdentifier, user);
            }
        } else if (identifier instanceof Long) {
            idCache.put((Long) identifier, user);
        }
    }

    // Public methods for testing cache contents
    public static User getFromAccountCache(String key) {
        return accountCache.get(key);
    }

    public static User getFromEmailCache(String key) {
        return emailCache.get(key);
    }

    public static User getFromIdCache(Long key) {
        return idCache.get(key);
    }

    public static void clearAllCaches() {
        accountCache.clear();
        emailCache.clear();
        idCache.clear();
    }
}

