import java.util.*;

public class PathAnalyzer {
    public static Map<String, Object> analyzePath(String filePath) {
        if (filePath == null || filePath.isEmpty()) {
            throw new IllegalArgumentException("File path cannot be null or empty");
        }

        Map<String, Object> result = new HashMap<>();
        String filename = filePath.substring(filePath.lastIndexOf('/') + 1);
        result.put("filename", filename);

        // Extract extension
        int dotIndex = filename.lastIndexOf('.');
        if (dotIndex > 0) { // Dot must not be at the beginning or absent
            result.put("extension", filename.substring(dotIndex + 1));
        } else {
            result.put("extension", "");
        }

        // Extract parent directory
        int lastSlashIndex = filePath.lastIndexOf('/');
        String parentDir;
        if (lastSlashIndex <= 0) {
            parentDir = "";
        } else {
            parentDir = filePath.substring(0, lastSlashIndex);
            // Normalize path by removing trailing slashes
            while (parentDir.endsWith("/") && parentDir.length() > 1) {
                parentDir = parentDir.substring(0, parentDir.length() - 1);
            }
        }
        result.put("parentDir", parentDir);

        // Calculate depth based on normalized parent directory
        int depth = 0;
        if (!parentDir.isEmpty()) {
            // Handle absolute paths (starting with '/')
            String pathToCount = parentDir;
            if (pathToCount.startsWith("/")) {
                pathToCount = pathToCount.substring(1);
            }

            // Count segments
            if (!pathToCount.isEmpty()) {
                String[] segments = pathToCount.split("/");
                depth = segments.length;
            }
        }
        result.put("depth", depth);

        return result;
    }
}

