import java.util.*;

class UserInfoManager {
    // Nested PhoneContact class
    static class PhoneContact {
        String name;
        String phoneNumber;

        public PhoneContact(String name, String phoneNumber) {
            this.name = name;
            this.phoneNumber = phoneNumber;
        }
    }

    // Nested UserInfo class
    static class UserInfo {
        String name;
        String phoneNumber;

        public UserInfo(String name, String phoneNumber) {
            this.name = name;
            this.phoneNumber = phoneNumber;
        }
    }

    // Method to process contacts
    public Map<String, Object> processContacts(List<PhoneContact> contacts) {
        Map<String, Object> result = new HashMap<>();
        List<UserInfo> processedUsers = new ArrayList<>();
        Set<String> seenPhoneNumbers = new HashSet<>();
        int duplicateCount = 0;

        // Process contacts in batches of 200
        int batchSize = 200;
        int batchesProcessed = 0;

        for (int i = 0; i < contacts.size(); i += batchSize) {
            batchesProcessed++;
            int endIndex = Math.min(i + batchSize, contacts.size());
            List<PhoneContact> batch = contacts.subList(i, endIndex);

            for (PhoneContact contact : batch) {
                if (!seenPhoneNumbers.contains(contact.phoneNumber)) {
                    seenPhoneNumbers.add(contact.phoneNumber);
                    processedUsers.add(new UserInfo(contact.name, contact.phoneNumber));
                } else {
                    duplicateCount++;
                }
            }
        }

        result.put("processedUsers", processedUsers);
        result.put("batchesProcessed", batchesProcessed);
        result.put("duplicateContacts", duplicateCount);
        return result;
    }
}

